<script setup>
import {ref} from 'vue'
import { useAnimate } from '@vueuse/core'
const el = ref()
const {
  isSupported,
  animate,

  // actions
  play,
  pause,
  reverse,
  finish,
  cancel,

  // states
  pending,
  playState,
  replaceState,
  startTime,
  currentTime,
  timeline,
  playbackRate,
} = useAnimate(el,   [
    { clipPath: 'circle(70% at 0% 40%)' },
    { clipPath: 'circle(70% at 50% 80%)' },
    { clipPath: 'circle(70% at 100% 30%)' },
  ],
  {
    duration: 5000,
    iterations: 10,
    direction: 'alternate',
    easing: 'cubic-bezier(0.46, 0.03, 0.52, 0.96)',
  },)
const name = ref("FULL-STACK WEB DEVELOPER")
let nameArr = name.value.split('')
let desc = ref()
let namee = ref([])
let i = 0 ;
let myintv = setInterval(()=>{
    namee.value.push(nameArr[i])
    desc.value = namee.value.toString()
    desc.value = desc.value.replace(/,/gi,"")
    
    i++
    if(nameArr.length == i){
        clearInterval(myintv)        
    }
},1000)


</script>

